import React, { useState, useEffect } from 'react';
import axios from 'axios';

let counter = 0

export default function SpellChecker() {
    const [word, setWordTerm] = useState('')
    const [text, setText] = useState('');
    const [typos, setTypos] = useState([]);

    const URL = 'http://localhost:8080'
    const handleTextChange = e => {
      setText(e.target.value);
      setWordTerm(e.target.value);
    };
    const handleTypoClick = (typo) => {
      setText(text => {
        const newText = text.slice(0, typo.start) + typo.suggestion + text.slice(typo.end);
        setWordTerm(newText);
        return newText;
      });
      setTypos(typos.filter(item => item !== typo));
    };


    useEffect(() => {
      const delayDebounceFn = setTimeout(() => {
        const customConfig = {
          headers: {
          'Content-Type': 'text/plain'
          }
        }
        if(word){
          axios.post(URL+"/message", word, customConfig).then(res => {
            setTypos(res.data.issues.map(typo => ({
              word: typo.match.surface,
              start: typo.match.beginOffset,
              end: typo.match.endOffset,
              suggestion: typo.match.replacement,
              type: typo.type
            })));
          }).catch(error => {
            console.error(error);
          });
        }

      }, 1000)
      return () => clearTimeout(delayDebounceFn)
    }, [word])

    return (
      <div className="float-container">

        <div className="float-child">
          <textarea
            autoFocus
            type='text'
            autoComplete='off'
            className='live-type-field'
            placeholder='Type here...'
            value={text}
            style={{width: "100%", height: "100%", fontFamily: 'Futura', fontSize: "18px"}}
            onChange={handleTextChange}
          />
        </div>

        <div className="float-child">
          {typos.map((typo, index) => (
            <div key={index} style={{display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '10px 0'}}>
              <p style={{margin: 0}}>Type: {typo.type}</p>
              <p style={{margin: 0}}>Word: {typo.word}</p>
              {typo.suggestion.map((suggestion, i) => (
                <button 
                  key={i}
                  style={{
                    padding: '10px 20px',
                    backgroundColor: 'lightgray',
                    color: 'black',
                    border: 'none',
                    borderRadius: '5px',
                    margin: '5px 0',
                    transition: 'background-color 0.2s ease-in-out',
                  }}
                  onClick={() => handleTypoClick(typo, suggestion)}
                  onMouseEnter={e => e.currentTarget.style.backgroundColor = 'gray'}
                  onMouseLeave={e => e.currentTarget.style.backgroundColor = 'lightgray'}
                >
                  Suggestion: {suggestion}
                </button>
              ))}
            </div>
          ))}
        </div>
      </div>

    )
  }
