import React, { useState, useEffect } from 'react';
import axios from 'axios';

let counter = 0

export default function SpellChecker() {
    const [word, setWordTerm] = useState('')
    const [text, setText] = useState('');
    const [typos, setTypos] = useState([]);

    const URL = 'http://localhost:8080'
    const handleTextChange = e => {
      setText(e.target.value);
      setWordTerm(e.target.value);
    };
    useEffect(() => {
      const delayDebounceFn = setTimeout(() => {
        const customConfig = {
          headers: {
          'Content-Type': 'text/plain'
          }
        }
        if(word){
          axios.post(URL+"/message", word, customConfig).then(res => {
            res.data.issues.map(typo => (
              console.log(typo.match.beginOffset + " " + typo.match.endOffset + " " + typo.match.replacement[0] + " " +typo.match.surface + " " +typo.type)
            ));
            setTypos(res.data.issues.map(typo => ({
              word: typo.match.surface,
              start: typo.match.beginOffset,
              end: typo.match.endOffset,
              suggestion: typo.match.replacement[0],
              type: typo.type
            })));
          }).catch(error => {
            console.error(error);
          });
        }

      }, 1000)
      return () => clearTimeout(delayDebounceFn)
    }, [word])

    return (
      <div className="float-container">

        <div className="float-child">
          <textarea
            autoFocus
            type='text'
            autoComplete='off'
            className='live-type-field'
            placeholder='Type here...'
            value={text}
            style={{width: "100%", height: "100%", fontFamily: 'Futura', fontSize: "18px"}}
            onChange={handleTextChange}
          />
        </div>

        <div className="float-child">
        {typos.map((typo, index) => (
        <button key={index} onClick={() => {
            setText(text.slice(0, typo.start) + typo.suggestion + text.slice(typo.end));
            setWordTerm(text);
          }}>
            Word: {typo.word} | Start: {typo.start} | End: {typo.end} | Suggestion: {typo.suggestion}
          </button>
        ))}
        </div>
      </div>

    )
  }
